import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Usuario } from 'models/usuario.model';
import { URLS_SERVICIOS } from 'src/app/config/config';
import { map } from 'rxjs/operators';
import Swal from 'sweetalert2';
import { Router } from '@angular/router';

@Injectable()
export class UsuarioService {

  usuario: Usuario | any;
  token: string | any;

  constructor(
    public http: HttpClient,
    public router: Router,
  ) { 
    this.cargarStorage()
  }

  estaLogueado(){
    return (this.token.length > 5)? true: false;
  }

  cargarStorage(){
    if (localStorage.getItem('token')) {  
      this.token = localStorage.getItem('token')
      this.usuario = localStorage.setItem('usuario', JSON.stringify(this.usuario));

    }else{
      this.token = '';
      this.usuario = null;
    }
  }

  guardarStorage(id: string, token:string, usuario: Usuario){
    localStorage.setItem('id', id);
    localStorage.setItem('token', token);
    localStorage.setItem('usuario', JSON.stringify(token));

    this.usuario = usuario;
    this.token = token;
  }

  logout(){
    this.usuario = null;
    this.token = '';

    localStorage.removeItem('token')
    localStorage.removeItem('usuario')

    this.router.navigate(['/login']);
  }

  loginGoogle(token: string){
    let url = URLS_SERVICIOS + '/login/google';

     return this.http.post(url, {token}).pipe(
      map((resp: any)=>{
        this.guardarStorage(resp.id, resp.token, resp.usuario )
        return true;
      })
     )


  }

  login(usuario: Usuario, recordar: boolean = false){

    if (recordar) {
      localStorage.setItem('email', usuario.email)
    }else{
       localStorage.removeItem('email');
    }


    let url = URLS_SERVICIOS + '/login';
     return this.http.post(url, usuario).pipe(
      map((resp: any)=>{
        this.guardarStorage(resp.id, resp.token, resp.usuario);

        return true;
      })
     )
  }

  crearUsuario(usuario: Usuario){
    let url = URLS_SERVICIOS + '/usuario';

    return this.http.post(url, usuario).pipe(
      map((res: any)=>{
        Swal.fire('Usuario creado', usuario.email , 'success');
        return res.usuario;
      })
    )
  }



}
